mapper.select:
select *
from cal_index_record
where cal_index = #{calIndex}
  and cal_vars = #{calIndexVars}
order by create_time desc limit 1

select *
from cal_index_record
where cal_index = #{calIndex}
  and cal_vars = #{calIndexVars}
  and status = 1
order by create_time desc limit 1

select *
from cal_index_record
where cal_index = #{calIndex}
  and cal_vars = #{calIndexVars}
  and status = 0
order by create_time desc limit 1
